import { createSlice} from "@reduxjs/toolkit";



// const initialState = {
//    cartProducts:[]
// }



const cartSlice = createSlice({
    name: 'cart',
    initialState:[],
    reducers: {
        addProductToCart: (state,action)=>{
            const check = state.find((eachProduct) => eachProduct.id === action.payload.id)
            if(check){
                return state.map((product) => product.id === action.payload.id ? {...product, qty:product.qty+1} : product)
            } else {
                return [
                    ...state,
                ]
            }
            
        },
        removeProductFromCart:(state,action)=>{
            return state.filter(product => product.id !== action.payload)
            
        }
    }
})


export default cartSlice.reducer
export const {addProductToCart} = cartSlice.actions
export const {removeProductFromCart} = cartSlice.actions
