import React from 'react'

const ProductCard = ({products}) => {
    
    if(!products) return 'Loading.....'
  return (
    
    <div style={{display:'flex', flexWrap:'wrap', gap:'10px', width:'80%' }}>
          
         {products.map(product => (
             
              <div key={product.id} className="card shadow d-flex flex" style={{width: '270px', height:"500"}}>
              <img className="card-img-top shadow" style={{width: '250px', height:"250px", border:'1px solid red', borderRadius:'5px', margin:'2px' }} src="https://tinyurl.com/59ynrhkt" alt="Card image cap"/>
              <div className="card-body">
               <h5 className="card-title">{product.productName}</h5>
               <p > {product.productVersion}</p>
               <h5 className='text-center'> {`${product.productPrice}/- Rs`}</h5>
               <i className='fa fa-shopping-cart bg-danger text-white rounded-5 p-1'></i>
    
               </div>
              </div>

            ))}

    </div>   
    
  )
}

export default ProductCard
