import React from 'react'
import { removeProductFromCart } from '../features/cartSlice'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'



const Cart = () => {
  const dispatch = useDispatch()
  const products = useSelector((state) => state.cart)
   
 
   console.log(products)
    
    if(!products) return 'Loading...'
   
  return (
    <div style={{display:'flex', flexWrap:'wrap', gap:'20px', width:'100%' }}>
   {products.length === 0 ? <div className='d-flex justify-content-center align-items-center' style={{width:'100%'}}> <h1> your cart is empty</h1> </div> : products.map((product )=> (

<div key={product.id} className="card shadow d-flex flex-column align-items-center" style={{width: '270px', height:"500", background:'#F3F3F3'}} >
<img className="card-img-top shadow" style={{width: '250px', height:"250px", border:'1px solid red', borderRadius:'5px', margin:'2px' }} src="https://tinyurl.com/59ynrhkt" alt="Card image cap"/>
   <div className='d-flex flex-column ' style={{width:'100%'}}>
      <div className="card-body align-self-start" >
       <h5 className="card-title " >{product.productName}</h5>
       <p > {product.productVersion}</p>
       </div>
       <div className='d-flex ' style={{justifyContent:'space-between'}}>
       <h5 className='text-center'> {`${product.productPrice}/- Rs`}</h5>
       <div onClick={() => dispatch(removeProductFromCart(product.id))}>
       <i className=''  />
       <button className='fa fa-shopping-bag bg-danger text-white p-2 mx-2 rounded border-0 addtocart'> Delete Item</button>
       </div>
       
       </div>
   </div>
</div>
  //  <div>
  //       <h1  key={product.id} onClick={() => dispatch(removeProductFromCart(product.id))}>{product.productName}</h1>
  //  </div>
   ))}
     

</div>
  )
}




export default Cart
