import React from 'react'
import './signup.css'

const Signup = () => {
  return (
    <div className='container mt-5'>
        <h1>Create a New Account</h1>
        <InputElement type='text' label='Name' placeholder='Enter your name' id='name' />
        <InputElement type='email' label='Email' placeholder='Enter your e-mail' id='email' />
        <InputElement type='password' label='Password' placeholder='Enter your Password' id='password' />
        <InputElement type='password' label='Confirm Password' placeholder='Enter your Password' id='confirm-password' />
       <div className='flex '>
        <button className='btn btn-primary ' type='submit' > SignUp</button>
        </div>
    </div>
  )
}

export default Signup

function InputElement ({label, placeholder, id, type,}) {
    return (
      <div className='input-div'>
      <label className='label' htmlFor={id} >{label}</label>
      <input id={id} type={type} className='form-control input-feild' placeholder={placeholder} />
  </div>
    )
}
