import {React, useEffect, useState} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { fetchProducts } from '../features/productsSlice'
import FetchApi from './fetchApi/FetchApi'
import ProductCard from './ProductCard'


const ProductList = () => {

  // const [products, setProducts] = useState([])
   const products = useSelector(state => state.products)
   const dispatch = useDispatch()
   const cartProducts = useSelector(state => state.cartProducts)
  
  useEffect(() => {
    // FetchApi('http://localhost:4000/products', 'GET').then(allProducts => setProducts(allProducts))
    dispatch(fetchProducts())
  }, [])
  
  // console.log(products)
  if(products.loading) return 'Loading...'
  return (
      //  <div className='d-flex justify-content-center' style={{width:'100%'}}>
      <div  style={{display:'flex', flexWrap:'wrap', gap:'10px', width:'100%', marginLeft:'5%' }}>
       <ProductCard products={products}/>
       {cartProducts && cartProducts.map(cpr => (<li> {cpr.productName}</li>)) }
      </div>
  )
}

export default ProductList
