import { createSlice} from "@reduxjs/toolkit";



// const initialState = {
//    cartProducts:[]
// }



const cartSlice = createSlice({
    name: 'cart',
    initialState:[],
    reducers: {
        addProductToCart: (state,action)=>{
            const product = action.payload
            const check = state.find((eachProduct) => eachProduct.id === product.id)
            if(check){
                return state.map((eachProduct) => eachProduct.id === product.id ? {...eachProduct, qty:eachProduct.qty+1} : product)
            } else {
                return [
                    ...state,
                    {
                        ...action.payload,
                        qty:1
                    }
                ]
            }
            
        },
        removeProductFromCart:(state,action)=>{
            const check = state.find((product) => product.id === action.payload.id)
             if(check.qty === 1){
                     return state.filter((product) => product.id === action.payload.id)
             }else{
                return state.map(product => product.id === action.payload.id ? {...product, qty: product.qty -1 } : product )
             }

           
            
        }
    }
})


export default cartSlice.reducer
export const {addProductToCart} = cartSlice.actions
export const {removeProductFromCart} = cartSlice.actions
